class TrafficLight {
  constructor(x, y, size) {
    this.x = x;
    this.y = y;
    this.size = size;
    this.color = color(50);
  }

  display() {
    fill(30);
    rect(this.x - this.size/2, this.y - this.size/2, this.size, this.size, 10);
    fill(this.color);
    ellipse(this.x, this.y, this.size * 0.8, this.size * 0.8);
  }

  setColor(c) {
    this.color = c;
  }
}

