```java
int state = 0; // 0: red, 1: green, 2: yellow
int timer = 0;
final int redDuration = 3000;
final int greenDuration = 3000;
final int yellowDuration = 1000;

void setup() {
  size(200, 500);
  noStroke();
  textAlign(CENTER, CENTER);
  textSize(24);
}

void draw() {
  background(50);

  // 绘制信号灯框架
