  // 绘制黄灯
  fill(255, 255, 0);
  ellipse(100, 220, 80, 80);
  fill(0);
  text("黄", 100, 220);

  // 绘制绿灯
  fill(0, 255, 0);
  ellipse(100, 340, 80, 80);
  fill(0);
  text("绿", 100, 340);

