      ellipse(100, 340, 80, 80);
      break;
    case 2:
      fill(255, 255, 0, 200);
      ellipse(100, 220, 80, 80);
      break;
  }

  // 更新计时器
  timer += millis() - lastTime;
  lastTime = millis();

  // 根据当前状态切换到下一个状态
  if (state == 0 && timer >= redDuration) {
    state = 1;
    timer = 0;
